import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import TodayIcon from '@material-ui/icons/Cloud';
import TomorrowIcon from '@material-ui/icons/WbSunny';
import TenDaysIcon from '@material-ui/icons/WbCloudy';

import SimpleExpansionPanel2 from "./expansionPanel2";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import AppBar from '@material-ui/core/AppBar';
import NoSsr from '@material-ui/core/NoSsr';
import Typography from '@material-ui/core/Typography';


const styles = {
    root: {
        position:'absolute',
        top:0,
        width:1000,
        alignSelf:'stretch',
        flex: 1,
        borderWidth: 1,
        flexDirection: 'row',
        flexWrap: 'wrap'



    }

};
function TabContainer(props) {
    return (
        <Typography component="div" style={{ padding: 8 * 3 }}>
            {props.children}
        </Typography>
    );
}
function LinkTab(props) {
    return <Tab component="a" onClick={event => event.preventDefault()} {...props} />;
}
class SimpleBottomNavigation extends React.Component {
    state = {
        value: 0,
    };

    handleChange = (event, value) => {
        this.setState({ value });

    };

    render() {
        const { classes } = this.props;
        const { value } = this.state;

        return (
            <NoSsr>
                <div className={classes.root}>
                    <AppBar position="static">
                        <Tabs variant="fullWidth" minWidth={200} maxWidth={500} value={value} onChange={this.handleChange}>
                            <LinkTab label="Page One" href="page1" />
                            <LinkTab label="Page Two" href="page2" />
                            <LinkTab label="Page Three" href="page3" />
                        </Tabs>
                    </AppBar>
                    {value === 0 && <TabContainer>Page One</TabContainer>}
                    {value === 1 && <TabContainer>Page Two</TabContainer>}
                    {value === 2 && <TabContainer><SimpleExpansionPanel2/>></TabContainer>}
                </div>
            </NoSsr>
            //
            // <BottomNavigationAction
            //     value={value}
            //     onChange={this.handleChange}
            //     showLabels
            //     className={classes.root}
            //     variant="fullWidth"
            //     label="Today" icon={<TodayIcon />}>
            //
            //     <BottomNavigationAction label="Today" icon={<TodayIcon />} />
            //         <BottomNavigationAction label="Tomorrow" icon={<TomorrowIcon />} onClick={'./expansionPanel2.js'}/>
            //
            //
            //     <BottomNavigationAction label="10 days" icon={<TenDaysIcon />} />
            // </BottomNavigation>
        );
    }
}

SimpleBottomNavigation.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleBottomNavigation);
