import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import TodayIcon from '@material-ui/icons/Cloud';
import TomorrowIcon from '@material-ui/icons/WbSunny';
import TenDaysIcon from '@material-ui/icons/WbCloudy';

import SimpleExpansionPanel2 from "./expansionPanel2";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
const styles = {
    root: {
        position:'absolute',
        bottom:0,
        width:1000,
        alignSelf:'stretch'

    }

};

class SimpleBottomNavigation extends React.Component {
    state = {
        value: 0,
    };

    handleChange = (event, value) => {
        this.setState({ value });

    };

    render() {
        const { classes } = this.props;
        const { value } = this.state;

        return (
            <Tabs
                value={this.state.value}
                indicatorColor="primary"
                textColor="primary"
                onChange={this.handleChange}
            >
                <Tab label="Active" />
                <Tab label="Disabled" disabled />
                <Tab label="Active" />
            </Tabs>
            //
            // <BottomNavigationAction
            //     value={value}
            //     onChange={this.handleChange}
            //     showLabels
            //     className={classes.root}
            //     variant="fullWidth"
            //     label="Today" icon={<TodayIcon />}>
            //
            //     <BottomNavigationAction label="Today" icon={<TodayIcon />} />
            //         <BottomNavigationAction label="Tomorrow" icon={<TomorrowIcon />} onClick={'./expansionPanel2.js'}/>
            //
            //
            //     <BottomNavigationAction label="10 days" icon={<TenDaysIcon />} />
            // </BottomNavigation>
        );
    }
}

SimpleBottomNavigation.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleBottomNavigation);
